var namespace_prefix = ''; 



var result = sforce.connection.describeGlobal(); 



var TOKEN_OBJ = "Party__c"; 



var sobjects = result.getArray("sobjects"); 







for(var i=0; i<sobjects.length; i++) 



{ 



var sobjName = sobjects[i].name; 



var str = sobjName.split("__"); 







//if there are 3 values there is a namespace 



if (str.length == 3) 



{ 



//Is it one of ours? 



var sobj = str[1].valueOf() +'__c'; 



if(sobj == TOKEN_OBJ) 



{ 



namespace_prefix = str[0].valueOf(); 



break; 



} 



} 



} 







if(namespace_prefix != "") 



{ 



namespace_prefix += '.'; 



} 







if('{!$User.vlocity_cmt__DocuSignEmail__c}' == null || '{!$User.vlocity_cmt__DocuSignEmail__c}' == "") 



{ 



alert('{!$User.Username}' + ' does not have valid DocuSign account.'); 



} 



else { 

  var result =   sforce.apex.execute(namespace_prefix+"ContractDocuSignResource","voidEnvelopes", {objectId: '{!Contract.Id}', senderEmail:'{!$User.vlocity_cmt__DocuSignEmail__c }'}); 

  if(result == 'OK')

  {

     window.location.reload();

  }

  else

  {

     alert(result);

  } 



}
