{!REQUIRESCRIPT("/soap/ajax/30.0/connection.js")}
{!REQUIRESCRIPT("/soap/ajax/30.0/apex.js")}

var namespace_prefix = '';
var result = sforce.connection.describeGlobal();
var TOKEN_OBJ = "Party__c";
var sobjects = result.getArray("sobjects");

for(var i=0; i<sobjects.length; i++) {
    var sobjName = sobjects[i].name;
    var str = sobjName.split("__");

    //if there are 3 values there is a namespace
    if (str.length == 3) {
        //Is it one of ours?
        var sobj = str[1].valueOf() +'__c';
        if(sobj == TOKEN_OBJ) {
            namespace_prefix = str[0].valueOf();
            break;
        }
    }
}

var simpleDecompControllerNamespace = '';
var simpleDecompPageNamespace = '';
if(namespace_prefix != "") {
    simpleDecompControllerNamespace = namespace_prefix + '.';
    simpleDecompPageNamespace = namespace_prefix + '__';
}

// trim ID because we're sending Order IDs to thor in 15-length
window.location.href = '/apex/' + simpleDecompPageNamespace + 'XOMViewOrderDecomposition?id={!LEFT(Order.Id, 15)}&decompose=true';
