WebLink.url:
{!REQUIRESCRIPT('//ajax.googleapis.com/ajax/libs/jquery/1.12.4/jquery.min.js')}
{!REQUIRESCRIPT('//ajax.googleapis.com/ajax/libs/jqueryui/1.12.1/jquery-ui.min.js')}
{!REQUIRESCRIPT("/soap/ajax/30.0/connection.js")}
{!REQUIRESCRIPT("/xdomain/xdomain.js")}
{!REQUIRESCRIPT("/soap/ajax/30.0/apex.js")}
try{
  jQuery(function() {
    /*Append the jQuery CSS CDN Link to the Head tag.*/
    jQuery('head').append('<link rel="stylesheet" href="//ajax.googleapis.com/ajax/libs/jqueryui/1.12.1/themes/start/jquery-ui.css" type="text/css" />');
    /*Create the HTML(DIV Tag) for the Dialog.*/

    var namespace_prefix = '';
    var result = sforce.connection.describeGlobal();
    var sobjects = result.getArray("sobjects");
    for(var i=0; i<sobjects.length; i++) {
    var sobjName = sobjects[i].name;
    var str = sobjName.split("__");
    //if there are 3 values there is a namespace
    if (str.length == 3){
    namespace_prefix = str[0].valueOf();
    }
    }

    var html = 
      '<div style="display:none;" id="dialog"><iframe style="border: 0px;" width="600px" height="800px" src="/apex/'+namespace_prefix+'__OrderPDF?id={!Order.Id}"></iframe></div>';
    /*Check if the Dialog(DIV Tag) already exists if not then Append the same to the Body tag.*/
    jQuery('body').append(html);
    /*Open the jQuery Dialog.*/ 
    jQuery( "#dialog" ).dialog({
      autoOpen: true,
      modal: true,
      width: 650,
      buttons: [
        {
          text: "OK",
          click: function() {
            $( this ).dialog( "close" );
          }
        }
      ],
      open: function (event, ui) {
        jQuery('.ui-dialog').css('z-index',103);
        jQuery('.ui-widget-overlay').css('z-index',102);
      },
    });
  }); 
}
catch(e){
  alert('An Error has Occured. Error: ' + e);
}
