WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/54.0/connection.js")}
            {!REQUIRESCRIPT("/soap/ajax/30.0/apex.js")}

            var namespace_prefix = '';
            var result = sforce.connection.describeGlobal();
            var TOKEN_OBJ = "Party__c";
            var sobjects = result.getArray("sobjects");

            for(var i=0; i<sobjects.length; i++) {
                var sobjName = sobjects[i].name;
                var str = sobjName.split("__");

                //if there are 3 values there is a namespace
                if (str.length == 3) {
                    //Is it one of ours?
                    var sobj = str[1].valueOf() +'__c';
                    if (sobj == TOKEN_OBJ) {
                        namespace_prefix = str[0].valueOf();
                        break;
                    }
                }
            }

            var planviewNSP = (namespace_prefix != "") ? namespace_prefix + '__' : '';

var orderId = '{!Order.Id }';
var planId = '{!Order.vlocity_cmt__OrchestrationPlanIdId__c}';
var thorPlanId = '{!Order.vlocity_cmt__OrchestrationPlanReferenceId__c}';
var fulfilmentStatus =  "{!Order.vlocity_cmt__FulfilmentStatus__c}";
if(planId !== null && planId !== '')
{
    parent.location.href = parent.location.origin+'/'+planId;
} else if (thorPlanId !== null && thorPlanId !== '')
{
   parent.location.href =  '/apex/' + planviewNSP +'OrchestrationPlanViewCommon?id=' + thorPlanId + '&orderId=' + orderId;
} else if(fulfilmentStatus =='Draft')
{
    alert('Order is not associated with any Orchestration Plan!');
} else
{
    parent.location.href =  '/apex/' + planviewNSP +'OrchestrationPlanViewCommon?id=' + orderId;
}
