var namespace_prefix = ''; 
var gettingnamespace = sforce.connection.query("SELECT NamespacePrefix FROM ApexClass where Name = 'ApplicationUtilities' LIMIT 1"); 
var getname = gettingnamespace.getArray("records"); 
if(getname.length > 0) 
{ 
namespace_prefix = getname[0].NamespacePrefix; 
}
if (namespace_prefix==null || namespace_prefix=='')
window.location = 'apex/DeepCloner?id={!Product2.Id}&childtypes=ProductRelationship__c---Product2Id__c,ProductEligibility__c,ProductAvailability__c,ProductChildItem__c---ParentProductId__c,AttributeAssignment__c---ObjectId__c';
else
window.location = 'apex/'+namespace_prefix+'__DeepCloner?id={!Product2.Id}&childtypes=ProductRelationship__c---Product2Id__c,ProductEligibility__c,ProductAvailability__c,ProductChildItem__c---ParentProductId__c,AttributeAssignment__c---ObjectId__c';
