{!REQUIRESCRIPT("/soap/ajax/30.0/apex.js")} 
var namespace_prefix = ''; 
var gettingnamespace = sforce.connection.query("SELECT NamespacePrefix FROM ApexClass where Name = 'ApplicationUtilities' LIMIT 1"); 
var getname = gettingnamespace.getArray("records"); 
if(getname.length > 0) 
{ 
namespace_prefix = getname[0].NamespacePrefix; 
} 
if (namespace_prefix==null || namespace_prefix=='') 
window.location = 'apex/AttributeListSelectToMergePage?Id=' + '{!vlocity_cmt__AttributeCategory__c.Id}'; 
else 
window.location = 'apex/'+namespace_prefix+'__AttributeListSelectToMergePage?Id=' + '{!vlocity_cmt__AttributeCategory__c.Id}'
