{!REQUIRESCRIPT("/soap/ajax/32.0/connection.js")}
{!REQUIRESCRIPT("/soap/ajax/32.0/apex.js")}

var namespace_prefix_dot = '';
var gettingnamespace = sforce.connection.query("SELECT NamespacePrefix FROM ApexClass where Name = 'NamespaceUtilities' LIMIT 1");
var getname = gettingnamespace.getArray("records");
if(getname.length > 0 && getname[0].NamespacePrefix)
{
    namespace_prefix_dot = getname[0].NamespacePrefix+".";
}

// Do not include Namespace in this object
var version = {versionId: "{!vlocity_cmt__CalculationMatrixVersion__c.Id}", headerIdField: 'CalculationMatrixId__c', childObjectType:'CalculationMatrixRow__c', childObjectMasterDetailField: 'CalculationMatrixVersionId__c', bulkFilter: "Name = \'Header\'"};

var result = sforce.apex.execute(namespace_prefix_dot+"VlocityVersionManager","incrementVersion", {versJSON: JSON.stringify(version) }  );
console.log(result);

if (result != null)
{
    window.location.href = '/' + result;
}
else
{
    alert('Creating a new Version Failed');
}
