WebLink.url:
var dataPackType = 'CalculationMatrix';
var objectId = '{!vlocity_cmt__CalculationMatrix__c.Id}';
var suggestedName = '{!JSENCODE(vlocity_cmt__CalculationMatrix__c.Name)}';

{!REQUIRESCRIPT("/soap/ajax/30.0/connection.js")}
{!REQUIRESCRIPT("/soap/ajax/30.0/apex.js")}

var namespace_prefix = '';
var result = sforce.connection.describeGlobal();
var TOKEN_OBJ = "VlocityDataPack__c";
var sobjects = result.getArray("sobjects");
for (var i=0; i<sobjects.length; i++) {
    var sobjName = sobjects[i].name;

    var str = sobjName.split("__");

    //if there are 3 values there is a namespace
    if (str.length == 3) {
        var sobj = str[1].valueOf() +'__c';
        if (sobj == TOKEN_OBJ) {
            namespace_prefix = str[0].valueOf();
        }
    }
}

if (namespace_prefix != "") {
    namespace_prefix += '__';
}

window.location = 'apex/'+namespace_prefix+'DataPacksHome?exportDataPackType='+dataPackType+'&exportData='+objectId+'&exportSuggestedName='+suggestedName;
