WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/32.0/connection.js")}
{!REQUIRESCRIPT("/soap/ajax/32.0/apex.js")}

var namespace_prefix_dot = '';
var gettingnamespace = sforce.connection.query("SELECT NamespacePrefix FROM ApexClass where Name = 'NamespaceUtilities' LIMIT 1");
var getname = gettingnamespace.getArray("records");
if(getname.length > 0 && getname[0].NamespacePrefix)
{
namespace_prefix_dot = getname[0].NamespacePrefix+".";
}

var result = sforce.apex.execute(namespace_prefix_dot+"VlocityVersionManager","incrementVersion2", {toIncrement: "{!vlocity_cmt__CalculationProcedureVersion__c.Id}", headerIdField:'CalculationProcedureId__c',childObjectType1:'CalculationProcedureStep__c', childObjectMasterDetailField1: 'CalculationProcedureVersionId__c',childObjectType2:'CalculationProcedureVariable__c', childObjectMasterDetailField2: 'CalculationProcedureVersionId__c'}  );
console.log(result);

if (result != null)
{
window.location.href = '/' + result;
}
else
{
alert('Creating a new Version Failed');
}
