WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/30.0/connection.js")}
{!REQUIRESCRIPT("/soap/ajax/30.0/apex.js")}

var namespace_prefix = '';
var gettingnamespace = sforce.connection.query("SELECT NamespacePrefix FROM ApexClass where Name = 'NamespaceUtilities' LIMIT 1");
var getname = gettingnamespace.getArray("records");

if(getname.length > 0)
{
    namespace_prefix = getname[0].NamespacePrefix;
}

var urlParams = 'PricingServiceSimulation?pricingServiceVersionId=' + '{!vlocity_cmt__CalculationProcedureVersion__c.Id}' + '&effectiveStartDate=' +   '{!vlocity_cmt__CalculationProcedureVersion__c.vlocity_cmt__StartDateTime__c}' +'&effectiveEndDate=' +   '{!vlocity_cmt__CalculationProcedureVersion__c.vlocity_cmt__EndDateTime__c}';

if (namespace_prefix==null || namespace_prefix=='')
    window.location = 'apex/' + urlParams;
else
    window.location = 'apex/'+namespace_prefix+'__' + urlParams;
