var gettingnamespace = sforce.connection.query("SELECT NamespacePrefix FROM ApexClass WHERE Name ='ApplicationUtilities' LIMIT 1");
var getname = gettingnamespace.getArray("records");
var classname = "ScreeningClassBuilder";
if(getname.length > 0)
{
var namespace_prefix = getname[0].NamespacePrefix;
if(namespace_prefix != null && namespace_prefix != '')
{
classname = getname[0].NamespacePrefix +"." + classname;
}
}
var res=sforce.apex.execute(classname,"BuildScreeningClass",{sRuleGroupId:"{!vlocity_cmt__ScreeningRuleGroup__c.Id  }"});
alert(res);
